import {
  Pressable,
  Text,
  View,
  Image,
  StyleSheet,
  TextInput,
  AccessibilityInfo,
  findNodeHandle
} from "react-native";
import { useFocusEffect } from '@react-navigation/native';
import { useEffect, useCallback, useState, useRef } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { Picker } from "@react-native-picker/picker";
import QRCode from "react-native-qrcode-svg";

import { useColorScheme } from "@/hooks/useColorScheme";
import { Colors } from "@/constants/Colors";
import { Link } from 'expo-router';

type Pass = {
  number: number;
  email: string;
  birthdate: Date;
  image: string;
  lastname: string;
  firstname: string;
  _id?: string;
  __v?: number;
};

export default function Pass() {
  const colorScheme = useColorScheme();
  
  const consultezRef = useRef<TouchableOpacity>(null);

  useFocusEffect(
    useCallback(() => {
      const setFocus = () => {
        setTimeout(() => {
          if (consultezRef.current) {
            const nodeHandle = findNodeHandle(consultezRef.current);
            if (nodeHandle) {
              AccessibilityInfo.setAccessibilityFocus(nodeHandle);
            }
          }
        }, 100);
      };
      setFocus();
    }, [])
  );

  const base64logo =
    "data:image/png;base64,";

  const [lastname, setLastname] = useState<string>("");
  const [firstname, setFirstname] = useState<string>("");
  const [email, setEmail] = useState<string>("");
  const [birthdate, setBirthdate] = useState<string>("");
  const [image, setImage] = useState<string>("");

  const [pass, setPass] = useState<Pass | null>(null);

  const [infosNeeded, setInfosNeeded] = useState<Array<string>>([
    "lastname",
    "firstname",
    "email",
    "birthdate",
    "image",
  ]);

  useFocusEffect(
    useCallback(() => {
      // Fonction pour charger toutes les données
      const loadData = async () => {
        const storedLastname = await AsyncStorage.getItem("name");
        if (storedLastname !== null) {
          setLastname(storedLastname);
          setInfosNeeded((prevInfos) =>
            prevInfos.filter((info) => info !== "lastname")
          );
        }

        const storedFirstname = await AsyncStorage.getItem("firstname");
        if (storedFirstname !== null) {
          setFirstname(storedFirstname);
          setInfosNeeded((prevInfos) =>
            prevInfos.filter((info) => info !== "firstname")
          );
        }

        const storedEmail = await AsyncStorage.getItem("email");
        if (storedEmail !== null) {
          setEmail(storedEmail);
          setInfosNeeded((prevInfos) =>
            prevInfos.filter((info) => info !== "email")
          );
        }

        const storedBirthdate = await AsyncStorage.getItem("birthdate");
        if (storedBirthdate !== null) {
          setBirthdate(storedBirthdate);
          setInfosNeeded((prevInfos) =>
            prevInfos.filter((info) => info !== "birthdate")
          );
        }

        const storedImage = await AsyncStorage.getItem("image");
        if (storedImage !== null) {
          setImage(storedImage);
          setInfosNeeded((prevInfos) =>
            prevInfos.filter((info) => info !== "image")
          );
        }

        const storedPass = await AsyncStorage.getItem("pass");
        if (storedPass !== null) {
          const parsedPass = JSON.parse(storedPass);
          setPass(parsedPass);
        }
      };

      loadData();

      const setFocus = async () => {
        setTimeout(() => {
          if (consultezRef.current) {
            const nodeHandle = findNodeHandle(consultezRef.current);
            if (nodeHandle) {
              AccessibilityInfo.setAccessibilityFocus(nodeHandle);
            }
          }
        }, 100);
      };

      setFocus();

      return () => { };
    }, [])
  );

  // useEffect(() => {
  //   if (infosNeeded.length === 0) {
  //     console.log("All infos are stored");
  //   } else {
  //     console.log("Infos missing: " + infosNeeded.join(", "));
  //   }
  // }, [infosNeeded]);

  // useEffect(() => {
  //   if (pass) {
  //     console.log(pass.number);
  //   }
  // }, [pass]);

  const handleGeneration = () => {
    AsyncStorage.setItem("name", lastname);
    AsyncStorage.setItem("firstname", firstname);
    AsyncStorage.setItem("email", email);
    AsyncStorage.setItem("birthdate", birthdate);
    AsyncStorage.setItem("image", image);
    setInfosNeeded([]);

    const pass = fetch("https://feffs.elioooooo.fr/pass/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        lastname: lastname,
        firstname: firstname,
        email: email,
        birthdate: birthdate,
        image: image,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        AsyncStorage.setItem("pass", JSON.stringify(data));
        setPass(data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  return (
    <>
      <View style={{ marginTop: 64, marginBottom: 36, paddingHorizontal: 20 }}
      >
        <View
          style={{
            
            flexDirection: "row",
            justifyContent: "flex-start",
            alignItems: "center",
          }}
          accessibilityRole="header"
          accessibilityLabel="En-tête de votre pass festival"
        >
          <View style={styles.logoContainer}>
            <Image
              style={styles.logo}
              source={require("@/assets/images/logo.png")}
              accessible={true}
              accessibilityLabel="Logo de l'application FEFFS"
              accessibilityRole="image"
            />
          </View>
          <View style={{ justifyContent: "center" }}>
            <Text
              style={[
                styles.welcomeText,
                { color: Colors[colorScheme ?? "light"].headerText },
              ]}
              ref={consultezRef}
              accessible={true}
              accessibilityRole="header"
              accessibilityLabel="Page de consultation votre pass festival"
              importantForAccessibility="yes"
              accessibilityElementsHidden={false}
              // accessibilityTraits="header"
            >
              Consultez
            </Text>
            <Text
              style={[
                styles.titleText,
                { color: Colors[colorScheme ?? "light"].headerText },
              ]}
            >
              Votre pass festival
            </Text>
          </View>
        </View>
      </View>
      <View
        style={{
          display: "flex",
          gap: 10,
          flexWrap: "wrap",
          maxWidth: "100%",
          padding: 20,
          paddingTop: 0,
          flex: 1,
        }}
      >
        {pass ? (
          <View
            style={{
              display: "flex",
              flexDirection: "column",
              gap: 10,
              padding: 20,
              borderRadius: 10,
              width: "100%",
              backgroundColor: Colors[colorScheme ?? "light"].background,
            }}

          >
            <View
              style={{
                display: "flex",
                flexDirection: "column",
                gap: 10,
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <Text
                style={{
                  color: Colors[colorScheme ?? "light"].text,
                  fontWeight: "900",
                  fontSize: 20,
                  textAlign: "center",
                  marginBottom: 10,
                }}
                accessible={true}
                accessibilityRole="header"
                accessibilityLabel={`Pass numéro ${pass.number}`}
              
              >
                Pass n° : {pass.number}
              </Text>
              <View
                style={{
                  padding: 10,
                  borderRadius: 10,
                  backgroundColor: "white",
                  marginBottom: 20,
                }}
              >
                <QRCode
                  logo={{ uri: base64logo }}
                  value={`https://feffs.elioooooo.fr/pass/get/${pass._id}`}
                  logoBackgroundColor="transparent"
                  size={150}
                />
              </View>
            </View>

            <View
              style={{
                display: "flex",
                flexDirection: "row",
                alignItems: "center",
                padding: 10,
                gap: 10,
              }}
            >
              <View
                style={{
                  display: "flex",
                  flexDirection: "row",
                  justifyContent: "center",
                  flex: 1,
                  gap: 10,
                }}
              >
                <Image
                  style={{ width: "35%", aspectRatio: 1, borderRadius: 5 }}
                  source={{ uri: pass.image }}
                />
                <View
                  style={{ display: "flex", flexDirection: "column", gap: 5, justifyContent: 'space-around' }}
                >
                  <Text style={{ color: Colors[colorScheme ?? "light"].text, fontWeight: "bold" }}
                    accessible={true}
                    accessibilityLabel={`Nom complet : ${pass.firstname} ${pass.lastname}`}>
                    {pass.lastname} {pass.firstname}
                  </Text>
                  <Text style={{ color: Colors[colorScheme ?? "light"].text }}
                    accessible={true}
                    accessibilityLabel={`Email : ${pass.email}`}
                  >
                    {pass.email}
                  </Text>
                  <Text style={{ color: Colors[colorScheme ?? "light"].text }}
                    accessible={true}
                    accessibilityLabel={`Date de naissance : ${new Date(pass.birthdate).toLocaleDateString("fr-FR")}`}
              >
                    Né le {new Date(pass.birthdate).toLocaleDateString("fr-FR")}
                  </Text>
                </View>
              </View>
            </View>

            <Pressable
              style={styles.submitButton}
              onPress={() => {
                AsyncStorage.removeItem("name");
                AsyncStorage.removeItem("firstname");
                AsyncStorage.removeItem("email");
                AsyncStorage.removeItem("birthdate");
                AsyncStorage.removeItem("image");
                AsyncStorage.removeItem("pass");
                setPass(null);
              }}
              accessible={true}
              accessibilityRole="button"
              accessibilityLabel="Supprimer mon Pass"
              accessibilityHint="Appuyez pour supprimer votre pass festival"
            >
              <Text style={styles.submitButtonText}>Supprimer mon Pass</Text>
            </Pressable>
          </View>
        ) : (
          <View style={[
            styles.container,
            {
              backgroundColor: Colors[colorScheme ?? "light"].background,
              borderColor: Colors[colorScheme ?? "light"].tint
            }
          ]}>
            <Text style={[styles.mainText, { color: Colors[colorScheme ?? "light"].headerText }]}
                accessible={true}
                accessibilityRole="header"
                accessibilityLabel="Oh non... pas encore de pass !"
            >Oh non... pas encore de pass ?</Text>
            <Text style={[styles.descriptionText, { color: Colors[colorScheme ?? "light"].headerText }]}
                accessible={true}
                accessibilityLabel="Pas de panique ! Rendez-vous à la billetterie pour obtenir votre pass et bénéficier de tous les super avantages !"
        >
              Pas de panique ! Rendez-vous à la billetterie pour obtenir votre pass et
              bénéficier de tout les super avantages !
            </Text>
            <Link href="/billeterie" style={styles.submitButton}
            accessible={true}
          accessibilityRole="link"
          accessibilityLabel="Aller à la Billetterie"
          accessibilityHint="Appuyez pour naviguer vers la billetterie et acheter un pass festival">
              <Text style={[styles.mainText, { color: Colors[colorScheme ?? "light"].headerText}]}>
                Aller à la Billetterie
              </Text>
            </Link>
          </View>
        )}
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    display: 'flex',
    flexDirection: 'column',
    gap: 10,
    padding: 20,
    borderRadius: 10,
    width: '100%',
    borderWidth: 1,
    shadowOffset: { width: 0, height: 2 },
    boxShadow: '300px 0px 50px rgba(255, 47, 0, 0.1)',
  },
  mainText: {
    fontSize: 18,
    fontWeight: 'bold',
    marginBottom: 5,
  },
  descriptionText: {
    fontSize: 14,
    lineHeight: 20,
    marginBottom: 15,
  },
  logoContainer: {
    height: 40,
    width: 40,
    borderRadius: 20,
    marginRight: 12,
    boxShadow: "0px 0px 80px rgba(255, 255, 255, 1)",
  },
  logo: {
    width: "100%",
    height: "100%",
    borderRadius: 20,
  },
  welcomeText: {
    fontSize: 14,
    lineHeight: 20,
    fontWeight: "bold",
  },
  titleText: {
    fontSize: 18,
    lineHeight: 20,
    fontWeight: "bold",
  },
  input: {
    borderStyle: "solid",
    borderColor: "rgba(206, 90, 75, 0.8)",
    backgroundColor: "rgba(206, 90, 75, 0.05)",
    borderWidth: 1,
    borderRadius: 10,
    padding: 15,
    width: "100%",
    fontSize: 16,
    marginBottom: 15,
    color: "#fff",
  },
  submitButton: {
    backgroundColor: "rgba(206, 90, 75, 0.3)",
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 25,
    alignItems: "center",
    marginTop: 10,
  },
  submitButtonText: {
    color: "rgba(206, 90, 75, 1)",
    fontSize: 16,
    fontWeight: "bold",
  },
});
